/**
 * global.d
 * ----------------------------------------------------------------------
 * Global module definitions for generic modules, so these imports won't throw
 * errors to the TypeScript compiler.
 *
 * @author    Fabio Y. Goto <lab@yuiti.dev>
 * @since     0.0.1
 */
declare module "*.gif";
declare module "*.jpeg";
declare module "*.jpg";
declare module "*.png";
declare module "*.svg";
declare module "*.webp";
declare module "*.sass";
declare module "*.scss";
declare module "*.css";
